
public class Relational_Logical {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =10, b=20;
		
		System.out.println(a==b);
		System.out.println(a!=b);
		System.out.println(a>=b);
		System.out.println(a<=b);
		System.out.println(a>b);
		System.out.println(a<b);
		System.out.println("==========================");
		System.out.println(a==b && a!=b);
		System.out.println(a==b || a!=b);
		System.out.println(!(a!=b));
		

	}

}
